/* import { useAuth0 } from "@auth0/auth0-react"; */
import React from "react";

/* const { user } = useAuth0; */

export const TopBar = () => {
  const d = new Date();
  const daysOfWeek = [
    "Sunday",
    "Monday",
    "Tuesday",
    "Wednesday",
    "Thursday",
    "Friday",
    "Saturday",
  ];
  const monthsOfYear = [
    "January",
    "February",
    "March",
    "April",
    "May",
    "June",
    "July",
    "August",
    "September",
    "October",
    "November",
    "December",
  ];

  return (
    <header className="flex justify-between w-10/12 bg-blue-500 font-medium text-3xl leading-10 rounded-b-3xl p-5 drop-shadow-2xl">
      <p className="text-white">Hi, Reda</p>
      <p className="">
        {daysOfWeek[d.getDay()]}, {monthsOfYear[d.getMonth()]} {d.getDate()}
      </p>
    </header>
  );
};
