import { useAuth0 } from "@auth0/auth0-react";
import React, { useEffect, useState } from "react";
import { CodeSnippet } from "../components/code-snippet";
import { PageLayout } from "../components/page-layout";
import { getProtectedResource } from "../services/message.service";

export const ConnectPage = () => {
  const [message, setMessage] = useState("");
  const [input, setInput] = useState("");
  const { user, getAccessTokenSilently } = useAuth0();

  useEffect(() => {
    let isMounted = true;

    const getMessage = async () => {
      const accessToken = await getAccessTokenSilently();
      const { data, error } = await getProtectedResource(accessToken, user.sub);

      if (!isMounted) {
        return;
      }

      if (data) {
        setMessage(JSON.stringify(data, null, 2));
      }

      if (error) {
        setMessage(JSON.stringify(error, null, 2));
      }
    };

    getMessage();
    console.log(input);
    return () => {
      isMounted = false;
    };
  }, [getAccessTokenSilently, user, input]);

  return (
    <PageLayout>
      <div className="content-layout">
        <h1 id="page-title" className="content__title">
          Connect Page
        </h1>
        <div className="content__body">
          <p id="page-description">
            <span>
              This page sends a message to the api and retrives extra data.
            </span>
            <span>
              <strong>Only authenticated users can access this page.</strong>
            </span>
          </p>
          <CodeSnippet title="recieved Data" code={message} />
        </div>
        <div>
          <h1 className="content__title">Text Input</h1>
          <input type="text" onChange={(e) => setInput(e.value)} />
          <button type="submit">Send</button>
        </div>
      </div>
    </PageLayout>
  );
};
